mapper.select:
select count(*) from ( select * from t_doctor_referral cases where cases.status in #{item}
and cases.case_range = #{caseRange}
)cases left join ( select * from t_doctor_referral_resources video where video.type = #{videoType} )video on cases.id = video.record_id join t_doctor_member doctor on doctor.id = cases.user_id
join ( select hospital_info.id as hospitalId, hospital_info.root_hospital_id as rootHospitalId from t_hospital_info hospital_info join ( select * from t_doctor_member member where member.id = #{userId} and member.status = #{doctorStatus} )member on member.hospital = hospital_info.name )hospital_info on hospital_info.hospitalId = cases.root_hospital_id or hospital_info.rootHospitalId = cases.root_hospital_id

select cases.id as id, cases.user_id as userId, doctor.avatar as avatar, doctor.doctor_name as doctorName, doctor.hospital as hospital, cases.type as caseType, cases.title as title, cases.patient_age as patientAge, cases.patient_sex as patientSex, cases.temperature as temperature, cases.heart_rate as heartRate, cases.breath as breath, cases.consciousness as consciousness, cases.high_pressure as highPressure, cases.low_pressure as lowPressure, cases.nasogastric_tube as nasogastricTube, cases.indwelling_catheter as indwellingCatheter, cases.superficial_vein as superficialVein, cases.deep_vein as deepVein, cases.picc as picc, cases.skin_type as skinType, cases.skin_desc as skinDesc, cases.nihss_total_fee as nihssTotalFee, cases.patient_sub as patientSub, cases.main_desc as mainDesc, cases.now_illness as nowIllness, cases.his_illness as hisIllness, cases.health_check as healthCheck, cases.assist_check as assistCheck, cases.case_range as caseRange, cases.create_time as createTime, cases.last_update_time as lastUpdateTime, cases.status as status, cases.read_count as readCount, cases.receive_count as receiveCount, cases.root_hospital_id as rootHospitalId, video.resource_id as videoId, ( select group_concat(image.resource_id) from t_doctor_referral_resources image where image.record_id = cases.id and image.type = #{imageType} )as imageList, ( select group_concat(nihss.index) from t_doctor_nihss_detail nihss where nihss.record_id = cases.id and nihss.type = #{nihssType} )as nihssIndexList, ( select group_concat(nihss.result) from t_doctor_nihss_detail nihss where nihss.record_id = cases.id and nihss.type = #{nihssType} )as nihssResultList from ( select * from t_doctor_referral cases where cases.status in #{item}
and cases.case_range = #{caseRange}
)cases left join ( select * from t_doctor_referral_resources video where video.type = #{videoType} )video on cases.id = video.record_id join t_doctor_member doctor on doctor.id = cases.user_id
join ( select hospital_info.id as hospitalId, hospital_info.root_hospital_id as rootHospitalId from t_hospital_info hospital_info join ( select * from t_doctor_member member where member.id = #{userId} and member.status = #{doctorStatus} )member on member.hospital = hospital_info.name )hospital_info on hospital_info.hospitalId = cases.root_hospital_id or hospital_info.rootHospitalId = cases.root_hospital_id
order by cases.id desc

