and cases.case_range = #{caseRange}
)cases left join ( select * from t_doctor_referral_resources video where video.type = #{videoType} )video on cases.id = video.record_id join ( select * from t_doctor_member doctor where doctor.id = #{userId} and doctor.status = #{doctorStatus} )doctor on doctor.id = cases.user_id

select count(*) from ( select * from t_doctor_referral cases where cases.status in #{item}
and cases.case_range = #{caseRange}
)cases left join ( select * from t_doctor_referral_resources video where video.type = #{videoType} )video on cases.id = video.record_id join ( select * from t_doctor_member doctor where doctor.id = #{userId} and doctor.status = #{doctorStatus} )doctor on doctor.id = cases.user_id left join t_doctor_referral_comment comment on comment.record_id = cases.id

select cases.id as id, cases.user_id as userId, doctor.avatar as avatar, doctor.doctor_name as doctorName, doctor.hospital as hospital, ifnull((comment.case_integrity+comment.case_detail),0) as doctorStarLevel, cases.type as caseType, cases.title as title, cases.patient_age as patientAge, cases.patient_sex as patientSex, cases.temperature as temperature, cases.heart_rate as heartRate, cases.breath as breath, cases.consciousness as consciousness, cases.high_pressure as highPressure, cases.low_pressure as lowPressure, cases.nasogastric_tube as nasogastricTube, cases.indwelling_catheter as indwellingCatheter, cases.superficial_vein as superficialVein, cases.deep_vein as deepVein, cases.picc as picc, cases.skin_type as skinType, cases.skin_desc as skinDesc, cases.nihss_total_fee as nihssTotalFee, cases.patient_sub as patientSub, cases.main_desc as mainDesc, cases.now_illness as nowIllness, cases.his_illness as hisIllness, cases.health_check as healthCheck, cases.assist_check as assistCheck, cases.case_range as caseRange, cases.create_time as createTime, cases.last_update_time as lastUpdateTime, cases.status as status, cases.read_count as readCount, cases.receive_count as receiveCount, cases.root_hospital_id as rootHospitalId, video.resource_id as videoId, ( select group_concat(image.resource_id) from t_doctor_referral_resources image where image.record_id = cases.id and image.type = #{imageType} )as imageList, ( select group_concat(nihss.index) from t_doctor_nihss_detail nihss where nihss.record_id = cases.id and nihss.type = #{nihssType} )as nihssIndexList, ( select group_concat(nihss.result) from t_doctor_nihss_detail nihss where nihss.record_id = cases.id and nihss.type = #{nihssType} )as nihssResultList from ( select * from t_doctor_referral cases where cases.status in #{item}
and cases.case_range = #{caseRange}
)cases left join ( select * from t_doctor_referral_resources video where video.type = #{videoType} )video on cases.id = video.record_id join ( select * from t_doctor_member doctor where doctor.id = #{userId} and doctor.status = #{doctorStatus} )doctor on doctor.id = cases.user_id left join t_doctor_referral_comment comment on comment.record_id = cases.id order by cases.id desc

