and cases.case_range = #{caseRange}
)cases left join ( select * from t_doctor_referral_resources video where video.type = #{videoType} )video on cases.id = video.record_id join ( select * from t_doctor_member doctor where doctor.status = #{doctorStatus} )doctor on doctor.id = cases.user_id join ( select recv.id as id, recv.record_id as record_id from ( select * from t_doctor_member member where member.id = #{userId} and member.status = #{doctorStatus} )member join ( select * from t_doctor_referral_recv recv where recv.status = #{receiveStatus} )recv on recv.user_id = member.id )recv on cases.id = recv.record_id

